// public/js/jquery-3.3.1.min.js - Minimal versiyon
window.$ = window.jQuery = function(selector) {
    const elements = typeof selector === 'string' ? document.querySelectorAll(selector) : [selector];
    
    return {
        length: elements.length,
        each: function(callback) {
            elements.forEach(callback);
            return this;
        },
        on: function(event, handler) {
            this.each(el => el.addEventListener(event, handler));
            return this;
        },
        ready: function(handler) {
            if (document.readyState !== 'loading') {
                handler();
            } else {
                document.addEventListener('DOMContentLoaded', handler);
            }
            return this;
        },
        addClass: function(className) {
            this.each(el => el.classList.add(className));
            return this;
        },
        removeClass: function(className) {
            this.each(el => el.classList.remove(className));
            return this;
        },
        toggleClass: function(className) {
            this.each(el => el.classList.toggle(className));
            return this;
        },
        css: function(property, value) {
            if (value === undefined && typeof property === 'string') {
                return elements[0] ? getComputedStyle(elements[0])[property] : undefined;
            }
            this.each(el => el.style[property] = value);
            return this;
        },
        animate: function(properties, duration, easing, complete) {
            this.each(el => {
                Object.keys(properties).forEach(prop => {
                    el.style.transition = `all ${duration}ms ${easing || 'ease'}`;
                    el.style[prop] = properties[prop];
                });
                
                if (complete) {
                    setTimeout(complete, duration);
                }
            });
            return this;
        },
        fadeIn: function(duration, complete) {
            this.each(el => {
                el.style.opacity = 0;
                el.style.display = 'block';
                
                setTimeout(() => {
                    el.style.transition = `opacity ${duration}ms`;
                    el.style.opacity = 1;
                    
                    if (complete) {
                        setTimeout(complete, duration);
                    }
                }, 10);
            });
            return this;
        },
        fadeOut: function(duration, complete) {
            this.each(el => {
                el.style.transition = `opacity ${duration}ms`;
                el.style.opacity = 0;
                
                setTimeout(() => {
                    el.style.display = 'none';
                    if (complete) complete();
                }, duration);
            });
            return this;
        },
        hide: function() {
            this.each(el => el.style.display = 'none');
            return this;
        },
        show: function() {
            this.each(el => el.style.display = '');
            return this;
        },
        html: function(content) {
            if (content === undefined) {
                return elements[0] ? elements[0].innerHTML : undefined;
            }
            this.each(el => el.innerHTML = content);
            return this;
        },
        text: function(content) {
            if (content === undefined) {
                return elements[0] ? elements[0].textContent : undefined;
            }
            this.each(el => el.textContent = content);
            return this;
        },
        val: function(value) {
            if (value === undefined) {
                return elements[0] ? elements[0].value : undefined;
            }
            this.each(el => el.value = value);
            return this;
        },
        find: function(selector) {
            const found = [];
            this.each(el => {
                found.push(...el.querySelectorAll(selector));
            });
            return $(found);
        },
        parent: function() {
            const parents = [];
            this.each(el => {
                if (el.parentElement) parents.push(el.parentElement);
            });
            return $(parents);
        },
        children: function() {
            const children = [];
            this.each(el => {
                children.push(...el.children);
            });
            return $(children);
        }
    };
};

// AJAX basit implementasyonu
$.ajax = function(options) {
    const xhr = new XMLHttpRequest();
    xhr.open(options.type || 'GET', options.url, true);
    
    xhr.onload = function() {
        if (xhr.status >= 200 && xhr.status < 300) {
            if (options.success) options.success(xhr.responseText);
        } else if (options.error) {
            options.error(xhr.statusText);
        }
    };
    
    xhr.onerror = function() {
        if (options.error) options.error('Network error');
    };
    
    xhr.send(options.data);
};